let birthdayDate=new Date("oct 4, 2024, 12:00:00"),timeStampBirthday=birthdayDate.getTime(),counterElement=document.querySelector(".hero__content__counter"),slidePolaroids=document.querySelector(".slide__polaroids");setInterval(()=>{var e=(new Date).getTime(),e=(timeStampBirthday-e)/1e3,t=Math.floor(e%60),o=Math.floor(e/60%60),r=Math.floor(e/3600)%24,e=Math.floor(e/86400);console.log(e,r,o,t),counterElement.innerHTML=e+`d ${r}h ${o}m ${t}s`},1e3),$(".slide__polaroids").slick({centerMode:!0,centerPadding:"60px",slidesToShow:4,arrows:!1,autoplay:!0,infinite:!0,speed:400,responsive:[{breakpoint:1024,settings:{arrows:!1,centerMode:!0,centerPadding:"40px",slidesToShow:3}},{breakpoint:560,settings:{arrows:!1,centerMode:!0,centerPadding:"40px",slidesToShow:2}}]});